const items = [];

for (const item of $input.all()) {
  const body = item.json.body;
  if (!body) continue;

  // üîß Funci√≥n para convertir markdown b√°sico a HTML
  const formatMarkdown = (text) => {
    return text
      .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>') // Negritas
      .replace(/\n/g, '<br>'); // Saltos de l√≠nea
  };

  // üîç Extraer t√≠tulo desde la secci√≥n Fecha
  const titleMatch = body.match(/\*\*[^]*?Fecha:\*\*\s*(.*?)\s{2,}/);
  const title = titleMatch ? titleMatch[1].trim() : "Acta de Reuni√≥n";

  // üîç Extraer hora de inicio
  const horaInicioMatch = body.match(/\*\*[^]*?Hora de inicio y fin:\*\*\s*(.*?)\s{2,}/);
  const horaInicio = horaInicioMatch ? horaInicioMatch[1].trim() : "";

  // üîß Funci√≥n robusta para extraer cualquier secci√≥n ignorando emojis u otros caracteres previos
  const getSection = (title) => {
    const regex = new RegExp(
      `\\*\\*[^\\*\\n]*?${title}:\\*\\*[\\s\\S]*?(?=\\n\\*\\*|\\n---|\\n$)`,
      "i"
    );
    const match = body.match(regex);
    return match
      ? match[0].replace(new RegExp(`\\*\\*[^\\*\\n]*?${title}:\\*\\*`, "i"), "").trim()
      : "";
  };

  const participantes = getSection("Participantes");
  const resumen = getSection("Resumen general de la clase");
  const citas = getSection("Intervenciones destacadas o citas relevantes");
  const observacion = getSection("Observaci√≥n sobre presencia del docente");
  const conclusion = getSection("Sugerencias para la mejora docente"); // T√≠tulo nuevo con emoji üåü

  const html = `<html>
  <body style="font-family: Arial, sans-serif; max-width: 800px; margin: 20px;">
    <h1 style="color: #2c3e50; border-bottom: 2px solid #3498db; padding-bottom: 10px;">Acta de Reuni√≥n - ${title}</h1>

    ${
      horaInicio
        ? `
    <h2 style="color: #2c3e50; margin-top: 20px;">Hora de inicio:</h2>
    <p style="margin-left: 20px;">${formatMarkdown(horaInicio)}</p>`
        : ""
    }

    ${
      participantes
        ? `
    <h2 style="color: #2c3e50; margin-top: 20px;">Participantes:</h2>
    <ul style="list-style-type: none; padding-left: 20px;">
    ${participantes
      .split("\n")
      .map((p) => `<li>${formatMarkdown(p.replace("- ", "").trim())}</li>`)
      .join("\n")}
    </ul>`
        : ""
    }

    ${
      resumen
        ? `
    <h2 style="color: #2c3e50; margin-top: 20px;">Resumen de la clase:</h2>
    <p style="margin-left: 20px;">${formatMarkdown(resumen)}</p>`
        : ""
    }

    ${
      citas
        ? `
    <h2 style="color: #2c3e50; margin-top: 20px;">Citas destacadas:</h2>
    <ul style="margin-left: 20px;">
    ${citas
      .split("\n")
      .filter((p) => p.trim() !== "")
      .map((p) => `<li>${formatMarkdown(p.trim().replace(/^[-‚Äì‚Äî]\s*/, ""))}</li>`)
      .join("\n")}
    </ul>`
        : ""
    }

    ${
      observacion
        ? `
    <h2 style="color: #2c3e50; margin-top: 20px;">Observaci√≥n sobre la presencia del docente:</h2>
    <p style="margin-left: 20px;">${formatMarkdown(observacion)}</p>`
        : ""
    }

    ${
      conclusion
        ? `
    <h2 style="color: #2c3e50; margin-top: 20px;">Sugerencias para la mejora docente:</h2>
    <p style="margin-left: 20px;">${formatMarkdown(conclusion)}</p>`
        : ""
    }

  </body>
</html>`;

  items.push({
    json: {
      html,
      to: item.json.to,
      subject: item.json.subject,
    },
  });
}

return items;

