const uniqueMeetingsMap = new Map();

items
  .sort((a, b) => new Date(b.json.created_at) - new Date(a.json.created_at))
  .forEach(item => {
    const now = new Date();
    const start = new Date(item.json.start_time);
    const end = new Date(start.getTime() + item.json.duration * 60000);

    let estado = '';
    if (now < start) {
      estado = 'pendiente';
    } else if (now >= start && now <= end) {
      estado = 'en curso';
    } else {
      estado = 'finalizado';
    }

    // Solo agrega si aÃºn no existe una reuniÃ³n con este ID
