const items = [];

for (const item of $input.all()) {
  const body = item.json.body;
  if (!body) continue;

  const formatMarkdown = (text) => {
    return text
      .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>') // Negrita
      .replace(/\n/g, '<br>'); // Saltos de lÃ­nea
  };

  const getSection = (sectionTitle) => {
    const regex = new RegExp(`###\\s*${sectionTitle}\\s*\\n([\\s\\S]*?)(?=\\n###|\\n---|$)`, 'i');
    const match = body.match(regex);
    return match ? match[1].trim() : '';
  };

  const titleMatch = body.match(/\*\*ðŸ“… Fecha:\*\*\s*(.*?)\s\s/);
  const title = titleMatch ? titleMatch[1].trim() : "Acta de ReuniÃ³n";

  const resumen = getSection("ðŸ“š Resumen general de la clase");
  const observacion = getSection("ðŸ”Ž ObservaciÃ³n sobre la presencia y participaciÃ³n del docente");
  const citas = getSection("ðŸ’¬ Intervenciones destacadas o citas relevantes");
  const conclusion = getSection("ðŸŒŸ Sugerencias para la mejora docente");

  const citasFormateadas = citas
    .split("\n")
    .filter(line => line.trim() !== "")
    .map(line => `<p style="margin: 5px 0; color: #000000;">${line.trim()}</p>`)
    .join("\n");

  const conclusionFormateada = formatMarkdown(
    conclusion.replace(/^> /gm, '').trim()
  );

  const html = `<html>
  <body style="font-family: Arial, sans-serif; max-width: 800px; margin: 20px; color: #000000;">
    <h1 style="color: #2c3e50; border-bottom: 2px solid #3498db; padding-bottom: 10px;">Acta de ReuniÃ³n - ${title}</h1>

    ${resumen ? `
      <h2 style="color: #2c3e50; margin-top: 20px;">Resumen de la clase:</h2>
      <p style="margin-left: 20px; color: #000000;">${formatMarkdown(resumen)}</p>
    ` : ""}

    ${observacion ? `
      <h2 style="color: #2c3e50; margin-top: 20px;">ObservaciÃ³n sobre la presencia del docente:</h2>
      <p style="margin-left: 20px; color: #000000;">${formatMarkdown(observacion)}</p>
    ` : ""}

    ${citas ? `
      <h2 style="color: #2c3e50; margin-top: 20px;">Citas destacadas:</h2>
      <div style="background-color: #f8f8f8; padding: 15px; border-radius: 5px; margin-left: 20px;">
        ${citasFormateadas}
      </div>
    ` : ""}

    ${conclusion ? `
      <h2 style="color: #2c3e50; margin-top: 20px;">Sugerencias para la mejora docente:</h2>
      <p style="margin-left: 20px; color: #000000;">${conclusionFormateada}</p>
    ` : ""}
  </body>
</html>`;

  items.push({
    json: {
      html,
      to: item.json.to,
      subject: item.json.subject,
    },
  });
}

return items;

