const object = $json;

// Convertir start_time UTC a hora local (PerÃº)
const startTimeUTC = new Date(object.start_time);

const options = {
  timeZone: "America/Lima",
  hour12: false,
  year: "numeric",
  month: "2-digit",
  day: "2-digit",
  hour: "2-digit",
  minute: "2-digit",
};

const startTimeLocal = startTimeUTC.toLocaleString("es-PE", options); // ejemplo: "27/06/2025, 10:30"

return {
  json: {
    meeting: {
      id: object.id,
      uuid: object.uuid,
      topic: object.topic,
      startTime: startTimeLocal,
      status: object.status,
      duration: object.duration,
      host: {
      id: object.host_id,
      email: object.host_email,
    },
    }
  },
};

const eventObject = $json.body.payload.object;
const participant = eventObject.participant;

// Convertir start_time UTC a hora local (PerÃº)
const joinTimeUTC = new Date(participant.join_time);

const options = {
  timeZone: "America/Lima",
  hour12: false,
  year: "numeric",
  month: "2-digit",
  day: "2-digit",
  hour: "2-digit",
  minute: "2-digit",
};

const joinTimeLocal = joinTimeUTC.toLocaleString("es-PE", options);

return {
  json: {
    meeting: {
      id: eventObject.id,
      uuid: eventObject.uuid,
    },
    participant: {
      id: participant.id,
      user_id: participant.user_id,
      participant_user_id: participant.participant_user_id || null,
      user_name: participant.user_name,
      email: participant.email || null,
      join_time: joinTimeLocal,
      isHost: participant.participant_user_id === eventObject.host_id,
    },
    host_id: eventObject.host_id,
  },
};

return items
  .sort((a, b) => new Date(b.json.created_at) - new Date(a.json.created_at))
  .map(item => {
    const now = new Date();
    const start = new Date(item.json.start_time);
    const end = new Date(start.getTime() + item.json.duration * 60000);

    let estado = '';

    if (now < start) {
      estado = 'pendiente';
    } else if (now >= start && now <= end) {
      estado = 'en curso';
    } else {
      estado = 'finalizado';
    }

    return {
      json: {
        ...item.json,
        estado,
      }
    };
  });
