return items
  .sort((a, b) => new Date(b.json.created_at) - new Date(a.json.created_at))
  .map(item => {
    const now = new Date();
    const start = new Date(item.json.start_time);
    const end = new Date(start.getTime() + item.json.duration * 60000);

    let estado = '';

    if (now < start) {
      estado = 'pendiente';
    } else if (now >= start && now <= end) {
      estado = 'en curso';
    } else {
      estado = 'finalizado';
    }

    return {
      json: {
        ...item.json,
        estado,
      }
    };
  });

